}

@article{clark2024real,
  title={Unsupervised Real-Time Hallucination Detection based on the Output of Language Models},
  author={Clark, D.},
  journal={ACL Anthology},
  year={2024},
  url={https://aclanthology.org/2024.findings-acl.854/}
}

@article{thompson2025guidelines,
  title={Improving Scientific Hypothesis Generation with Knowledge Grounded Approaches},
  author={Thompson, S.},
  journal={arXiv},
  year={2025},
  url={https://arxiv.org/html/2411.02382v1}
}

@article{morris2024study,
  title={Study uses large language models to sniff out hallucinations},
  author={Morris, E.},
  journal={The Register},
  year={2024},
  url={https://www.theregister.com/2024/06/20/llm_hallucination_study/}
}
